import { createGlobalStyle } from 'styled-components';

const GlobalStyles = createGlobalStyle`

*{
    padding: 0;
    margin: 0;
    box-sizing: border-box;
    scroll-behavior: smooth;
}

html {
    background-color: black;
    height: 100vh;
    border-radius: 100;
}

`;

export default GlobalStyles;
