import React, { useState, useEffect } from 'react';
import Exchange from './components/Exchange';
import axios from 'axios';

function App() {
  const [coins, setCoins] = useState([]);
  const [kraken_BTC, set_Kraken_BTC] = useState(0);
  const [kraken_ETH, set_Kraken_ETH] = useState(0);

  useEffect(() => {
    const getCoins = async () => {
      const { data: kraken_Coins } = await axios.get('/api/kraken/prices');
      setCoins(kraken_Coins.kraken_BTC.result.XXBTZUSD.p[0]);
      set_Kraken_BTC(kraken_Coins.kraken_BTC.result.XXBTZUSD.p[0]);
      set_Kraken_ETH(kraken_Coins.kraken_ETH.result.XETHZUSD);
    };
    getCoins();
  }, []);

  console.log(kraken_BTC);
  // console.log(coins.kraken_BTC.result.XXBTZUSD.p[0]);
  return (
    <div className='App'>
      <header className='App-header'>
        <p>Testing APP</p>
      </header>
    </div>
  );
}

export default App;
