import { createGlobalStyles } from 'styled-components';

const GlobalStyles = createGlobalStyles`

*{
    padding: 0;
    margin: 0;
    box-sizing: border-box;
    scroll-behavior: smooth;
}

`;

export default GlobalStyles;
